import React, { useReducer } from "react";

import CartContext from "./CartContext";

const defaultCartState = { items: [], totalAmount: 0 };

const cartReducer = (state, action) => {
  if (action.type === "ADD") {
    let updatedItems = [];
    if (!state.items.find((entry) => entry.id === action.item.id)) {
      updatedItems = state.items.concat(action.item);
    } else {
      const index = state.items.findIndex(
        (entry) => entry.id === action.item.id
      );
      const Original = [...state.items];
      Original[index].quantity += action.item.quantity;
      updatedItems = Original;
    }

    const updatedTotalAmount =
      state.totalAmount + action.item.price * action.item.quantity;
    
    return {
      items: updatedItems,
      totalAmount: updatedTotalAmount,
    };
  }

  // Remove Logic
  if (action.type === "REMOVE") {
    let updatedItems = [];
    let updatedTotalAmount;
    // getting index of item to be removed
    const existingItemIndex = state.items.findIndex(
      (entry) => {
        return entry.id === action.id
      }
    );
    
    // if quantity of item in cart is 1
    if (state.items[existingItemIndex].quantity === 1) {
      console.log(state.items)
      updatedItems = state.items.filter((entry) => entry.id !== action.id);
    }
    // if quantity of item in cart is greater than 1
    else {
      const ItemsList = [...state.items];
      ItemsList[existingItemIndex].quantity -= 1;
      updatedItems = ItemsList;
      updatedTotalAmount =
        state.totalAmount - state.items[existingItemIndex].price;
    }
    return { updatedItems, updatedTotalAmount };
  }
  return defaultCartState;
};

const CartProvider = (props) => {
  const [cartState, dispatchCartAction] = useReducer(
    cartReducer,
    defaultCartState
  );

  function addItemToCartHandler(item) {
    dispatchCartAction({ type: "ADD", item: item });
  }

  function removeItemToCartHandler(id) {
    dispatchCartAction({ type: "REMOVE", id: id });
  }

  return (
    <CartContext.Provider
      value={{
        items: cartState.items,
        totalAmount: cartState.totalAmount,
        addItem: addItemToCartHandler,
        removeItem: removeItemToCartHandler,
      }}>
      {props.children}
    </CartContext.Provider>
  );
};
export default CartProvider;
