import React, { useContext } from "react";

import classes from "./Cart.module.css";
import CartContext from "../store/CartContext";
import CartItem from './CartItem'
import Modal from "./Modal";

function Cart(props) {
  const CartCtx = useContext(CartContext);

  const cartItems = (
    <ol>
      {CartCtx.items.map((item) => (
        <li>
          <CartItem name={item.name} price={item.price} quantity={ item.quantity} onRemove={CartCtx.removeItem} onAdd={CartCtx.addItem} />
        </li>
      ))}
    </ol>
  );

  return (
    <Modal onClick={props.onClose}>
    
      <span className={classes.totalAmtLabel}>Total Amount</span>
      <span className={classes.totalAmt}>${CartCtx.totalAmount}</span>
      <div>
        <button
          className={`btn btn-secondary ${classes.closeBtn}`}
          onClick={props.onClose}>
          Close
        </button>
        <button
          className={`btn btn-warning ${classes.orderBtn}`}
          onClick={console.log("Ordering...")}>
          Order
        </button>
      </div>
    </Modal>
  );
}

export default Cart;
