import React, {useState, useContext, useEffect} from "react";
import CartContext from "../store/CartContext";
import CartButton from "../UI/CartButton";
import classes from './Header.module.css';

function Header(props) {
  const [BtnisHighlighted, setBtnisHighlighted] = useState(false);
  const CartCtx = useContext(CartContext);
  const { items } = CartCtx;

  const btnClasses = `btn btn-primary ${BtnisHighlighted ? classes.Bump : ''}`
  
  useEffect(() => {
    setBtnisHighlighted(true)
    const timerid = setTimeout(setBtnisHighlighted(false), 300);
    return () => {
      clearTimeout(timerid);
    }
  }, [items])
  

  const totalQuantity = CartCtx.items.reduce((currNum, item) => {
    return currNum + item.quantity;
  },0);
  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container-fluid border border-4 rounded-3">
          <h2>Meal Application</h2>
          <CartButton className={btnClasses} amount={totalQuantity} showCart={props.onShowCart}>
            Cart
          </CartButton>
        </div>
      </nav>
    </div>
  );
}

export default Header;
