import React, { useState, useEffect, useReducer } from "react";

import Card from "../UI/Card/Card";
import classes from "./Login.module.css";
import Button from "../UI/Button/Button";

const formReducer = (state, action) => {
  if (action.type === "USER_INPUT_EMAIL") {
    state[0].emailvalue = action.val;
    state[0].emailIsValid = action.val.include;
    return state;
  }
  if (action.type === "INPUT_BLUR_EMAIL") {
    state[1].passwordIsValid = action.val.trim().length > 6;
    return state;
  }

  if (action.type === "USER_INPUT_PASSWORD") {
    state[1].passwordIsValid = action.val;
    state[1].passwordIsValid = action.val.trim().length > 6;
    return state;
  }
  if (action.type === "INPUT_BLUR_PASSWORD") {

    state[1].passwordIsValid = action.val.trim().length > 6;
    return state;
  }
  return [
    { passwordvalue: "", passwordIsValid: null },
    { emailvalue: "", emailIsValid: null },
  ];
};

const Login = (props) => {
  const [formIsValid, setFormIsValid] = useState(false);
  const [formState, dispatchForm] = useReducer(formReducer, [
    { passwordvalue: "", passwordIsValid: null },
    { emailvalue: "", emailIsValid: null },
  ]);

  let emailIsValid = formState[0].emailIsValid;
  let passwordIsValid = formState[1].passwordIsValid;

  useEffect(() => {
    const timerid = setTimeout(() => {
      setFormIsValid(emailIsValid && passwordIsValid);
    }, 1000);
    return () => {
      clearTimeout(timerid);
    };
  }, [emailIsValid, passwordIsValid]);
  const emailChangeHandler = (event) => {
    dispatchForm({ type: "USER_INPUT_EMAIL", val: event.target.value });
  };

  const passwordChangeHandler = (event) => {
    dispatchForm({ type: "USER_INPUT_PASSWORD", val: event.target.value });
  };

  const validateEmailHandler = () => {
    dispatchForm({ type: "INPUT_BLUR_EMAIL" });
  };

  const validatePasswordHandler = () => {
    dispatchForm({ type: "INPUT_BLUR_PASSWORD" });
  };

  const submitHandler = (event) => {
    event.preventDefault();
    localStorage.setItem("isLoggedIn", 1);
    props.onLogin(formState[1].emailvalue, formState[1].passwordvalue);
  };

  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        <div
          className={`${classes.control} ${
            emailIsValid === false ? classes.invalid : ""
          }`}>
          <label htmlFor="email">E-Mail</label>
          <input
            type="email"
            id="email"
            value={formState[0].emailvalue}
            onChange={emailChangeHandler}
            onBlur={validateEmailHandler}
          />
        </div>
        <div
          className={`${classes.control} ${
            passwordIsValid === false ? classes.invalid : ""
          }`}>
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={formState[1].passwordvalue}
            onChange={passwordChangeHandler}
            onBlur={validatePasswordHandler}
          />
        </div>
        <div className={classes.actions}>
          <Button type="submit" className={classes.btn} disabled={!formIsValid}>
            Login
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default Login;
