import React, { useState } from "react";
import "./Form.css";
import ErrorModal from "./ErrorModal";

function Form(props) {
  const [Username, setUsername] = useState("");
  const [Userage, setUserage] = useState("");
  const [errorState, setErrorState] = useState();

  function nameChangeHandler(event) {
    setUsername(event.target.value);
  }
  function ageChangeHandler(event) {
    setUserage(event.target.value);
  }

  function AddUserHandler(event) {
    event.preventDefault();
    if (Userage.trim().length === 0 || Username.trim().length === 0) {
      setErrorState({ title: "Empty Input", message: "Enter a valid input!" });
    }
    if (+Userage < 1) {
      setErrorState({ title: "Invalid age", message: "Enter age > 0" });
    }

    const userData = {
      key: Math.random().toString(),
      name: Username,
      age: Userage,
    };
    props.onAddUserData(userData);
    setUserage("");
    setUsername("");
  }
  function onConfirm() {
    setErrorState(null);
  }
  return (
    <div>
      <form>
        <div className="form container">
          <div className="username m-3">
            <label htmlFor="exampleFormControlInput1" className="form-label">
              Username
            </label>
            <input
              type="text"
              className="form-control"
              id="exampleFormControlInput1"
              placeholder="name"
              value={Username}
              onChange={nameChangeHandler}
            />
          </div>
          <div className="age m-3">
            <label htmlFor="exampleFormControlInput1" className="form-label">
              Age
            </label>
            <input
              type="number"
              className="form-control"
              id="exampleFormControlInput1"
              placeholder="age"
              max={100}
              min={18}
              value={Userage}
              onChange={ageChangeHandler}
            />
          </div>
          <div className="m-3">
            <button
              className="btn btn-primary"
              type="submit"
              onClick={AddUserHandler}>
              Add User
            </button>
          </div>
        </div>
      </form>
      {/* {errorState &&  */}
        <ErrorModal
          title={errorState.title}
          message={errorState.message}
          onConfirm={onConfirm}
        />
      {/* } */}
    </div>
  );
}

export default Form;
