import { useState } from "react";


function BinaryTree() {
  const [tree, setTree] = useState({
    value: 1,
    left: null,
    right: null,
  });
  const [inputVal, setInputVal] = useState('');


  function insert(value) {
    const newNode = {
      left: null,
      value: value,
      right: null,
    };

    // if (tree === null) {
    //   setTree(newNode);
    //   return;
    // }

    let currentNode = tree;

    while (true) {
      if (value < currentNode.value) {
        if (currentNode.left == null) {
          currentNode.left = newNode;
          break;
        }
        currentNode = currentNode.left;

        continue;
      } else {
        if (value === currentNode.value) {
          break;
        } else {
          if (currentNode.right == null) {
            currentNode.right = newNode;
            break;
          }
          currentNode = currentNode.right;
          continue;
        }
      }
    }

    setTree(tree);
  }

function traverse(node) {
  if (node !== null) {
    return (
      <div>
        <div style={{width:'20px', height:'20px', marginLeft:'50%', border:'2px solid black', borderRadius:'5px'}}>{node.value}</div>
        <div style={{display:'flex', flexDirection:"row", justifyContent:'space-evenly'}}>
        <div >{traverse(node.left)}</div>
          <div >{traverse(node.right)}</div>
          </div>
      </div>
    );
  }
  }
  function onSubmitHandler(event) {
    event.preventDefault();
    insert(Number(inputVal));
    setInputVal('');
    console.log(tree);
  }
  function onChangeHandler(event) {
    setInputVal(event.target.value);
  }

  return (
    <div>
      {traverse(tree)}
      <hr/>
      <form onSubmit={onSubmitHandler} style={{ 'marginTop':'20px' }} >
        <input type="number" placeholder="enter the value you want to insert" onChange={onChangeHandler} value={inputVal } />
          <button type="submit">Insert</button>
      </form>
    </div>
  );
}

export default BinaryTree;
