import React, { useEffect, useState } from "react";

function Stack() {
  useEffect(() => {
    function Node(value) {
      this.value = value;
      // this.left = null;
      // this.right = null;
    }

    function insertNode(Tree, value) {
      var node = Tree,
        key;
      while (node.value !== value) {
        key = value < node.value ? "left" : "right";
        if (!node[key]) {
          node[key] = new Node(value);
          break;
        }
        node = node[key];
      }
      return Tree;
    }
    function traverse(node) {
      if (node !== null) {
        return (
          <div>
            <div
              style={{
                width: "20px",
                height: "20px",
                marginLeft: "50%",
                marginBottom: "7px",
                border: "2px solid black",
                borderRadius: "5px",
              }}>
              {node.value}
            </div>
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                justifyContent: "space-evenly",
              }}>
              <div style={{ marginRight: "5px" }}>{traverse(node.left)}</div>
              <div>{traverse(node.right)}</div>
            </div>
          </div>
        );
      }
    }

    var array = [8, 10, 12, 5, 3, 6];
    var Tree = array.reduce(
      (t, v) => (t ? insertNode(t, v) : new Node(v)),
      null
    );
    traverse(Tree);
  }, []);

  
}

export default Stack;
