import "./App.css";
import NodeItem from "./NodeItem";

function App() {
  // const [Nodes, setNodes] = useState([1, 2, 3]);
  const Tree = Array(3).fill(null);

  function root(key) {
    if (tree[0] != null) {
      console.log("Tree already had root");
    } else {
      tree[0] = key;
    }
  }

  function setLeft(key, parent) {
    if (tree[parent] == null) {
      console.log(`Can't set child at ${parent * 2 + 1}, no parent found <br>`);
    } else {
      tree[parent * 2 + 1] = key;
    }
  }

  function setRight(key, parent) {
    if (tree[parent] == null) {
      console.log(`Can't set child at ${parent * 2 + 2}, no parent found <br>`);
    } else {
      tree[parent * 2 + 2] = key;
    }
  }

  function printTree() {
    for (let i = 0; i < 10; i++) {
      if (tree[i] != null) {
        console.log(tree[i]);
      } else {
        console.log("-");
      }
    }
  }

