  useEffect(() => {
    function Node(value) {
      this.value = value;
      // this.left = null;
      // this.right = null;
    }

    function insertNode(Tree, value) {
      var node = Tree,
        key;
      while (node.value !== value) {
        key = value < node.value ? "left" : "right";
        if (!node[key]) {
          node[key] = new Node(value);
          break;
        }
        node = node[key];
      }
      return Tree;
    }

    function traverse(node) {
      console.log("value:"+node.value);
        node.left && console.group("left:")
        traverse(node.left);
      console.groupEnd();
      node.right && console.group("right:")&& traverse(node.right);
      console.groupEnd();
    }

    var array = [8, 10, 12, 5, 3, 6];
    var Tree = array.reduce(
      (t, v) => (t ? insertNode(t, v) : new Node(v)),
      null
      );
      console.group('Root');
      traverse(Tree);
      console.groupEnd();
  }, []);
}

export default Stack;
