import "./App.css";
import React, { useState } from "react";
import UsersList from "./UsersList";

function App() {
  const [userList, setUserList] = useState([{ id: Math.random().toString(), name: 'jod' }, { id: Math.random().toString(), name: 'justin' }, { id: Math.random().toString(), name: 'jatin' }]);
  const [userName, setUserName] = useState('');
  const addUserHandler = () => {
    setUserList((prevUserList) => {
      const data = { id: Math.random().toString(), name: userName };
      return [...prevUserList, data];
    });
  };

  const userNameChangeHandler = (event) => {
    setUserName(event.target.value);
  }

  const onDeleteHandler=(id)=>{
    setUserList((prevUserList) => {
      const UpdatedList= prevUserList.filter((item) => item.id !== id);
      return UpdatedList;
    });
  }
  return (
    <div className="container-fluid">
      <div></div><h1>Users List</h1>
      <input
        type="text"
        name="username"
        id="username"
        onChange={userNameChangeHandler}
      />div>
      <button onClick={addUserHandler}>Add</button>
      <UsersList UsersList={userList} onDeleteHandler={ onDeleteHandler} />
    </div>
  );
}

export default App;
