import "./App.css";
import React, { useState } from "react";
import UsersList from "./UsersList";

function App() {
  const [userList, setUserList] = useState([]);
  const [userName, setUserName] = useState('');
  const [count, setCount] = useState(0);
  const addUserHandler = () => {
    userName&&setUserList((prevUserList) => {
      const data = { id: Math.random().toString(), name: userName };
      return [...prevUserList, data];
    });
    setUserName('');
    setCount((prevCount)=>{prevCount++});
  };

  const userNameChangeHandler = (event) => {
    setUserName(event.target.value);
  }

  const onDeleteHandler=(id)=>{
    setUserList((prevUserList) => {
      const UpdatedList= prevUserList.filter((item) => item.id !== id);
      return UpdatedList;
    });
    setCount(userList.length);
  }
  return (
    <div className="container-fluid">
      <h1>Users List</h1>
      <h2>current users:{ count }</h2>
      <div>
        <input
          type="text"
          name="username"
          id="username"
          onChange={userNameChangeHandler}
          value={userName}
        />
        <button onClick={addUserHandler}>Add</button>
      </div>
      <UsersList UsersList={userList} onDeleteHandler={onDeleteHandler} />
    </div>
  );
}

export default App;
