import "./App.css";
import React, { useEffect, useState } from "react";
import UsersList from "./UsersList";
import List from "./List";

function App() {
  const [num, setNum] =useState(true);
  const [list, setList] = useState(List[0]);
  const [outputUserList, setOutputUserList] = useState(list);
  // console.log(list);
  useEffect(() => {
    let intervalId = setInterval(() => {
      setList(List[+num]);
      setNum(()=>!num);
      console.log(list);
    }, 5000);

    return () => {
      clearInterval(intervalId);
    }
  }, []);




  const onInputChangeHandler = (event) => {
    const searchValue = event.target.value.trim().toLowerCase();
    setOutputUserList(
      list.filter((item) => {
        const Name = `${item.name.salutation} ${item.name.first} ${item.name.last}`;
        return Name.toLowerCase().includes(searchValue);
      })
    );
  };

  return (
    <div className="container-fluid">
      <input type="text" placeholder="Search" onChange={onInputChangeHandler} />
      <UsersList outputUsersList={outputUserList} />
    </div>
  );
}

export default App;
