import "./App.css";
import React, { useState } from "react";
import UsersList from "./UsersList";

function App() {
  const userList = [
    {
      id: Math.random().toString(),
      name: {
        salutation: "Mr.",
        first: "Jatin",
        last: "Patel",
      },
    },
    {
      id: Math.random().toString(),
      name: {
        salutation: "Mrs.",
        first: "Anjali",
        last: "Shah",
      },
    },

    {
      id: Math.random().toString(),
      name: {
        salutation: "Mr.",
        first: "Darsh",
        last: "Gajjar",
      },
    },

    {
      id: Math.random().toString(),
      name: {
        salutation: "Mrs.",
        first: "Ishita",
        last: "Verma",
      },
    },

    {
      id: Math.random().toString(),
      name: {
        salutation: "Mr.",
        first: "Devarsh",
        last: "Prajapati",
      },
    },
  ];

  const [outputUserList, setOutputUserList] = useState(userList);

  const onInputChangeHandler = (event) => {
    const searchValue = console.log(event.target.value.trim().split(' '));
    setOutputUserList(
      userList.filter(
        (item) =>
          item.name.salutation.includes(searchValue.map((naam) => naam)) ||
          item.name.first.includes(searchValue.map((naam) => naam)) ||
          item.name.last.includes(searchValue.map((naam) => naam))
      )
    );
  };

  return (
    <div className="container-fluid">
      <input type="text" placeholder="Search" onChange={onInputChangeHandler} />
      <UsersList outputUsersList={outputUserList} />
    </div>
  );
}

export default App;
