import "./App.css";
import React, { useState } from "react";
import UsersList from './UsersList';

function App() {
  const [userList, setUserList] = useState(['jod', 'justin', 'jatin']);
  const [userName, setUserName] = useState('');
  const addUserHandler = () => {
    setUserList((prevUserList) => {
      return [...prevUserList, userName];
    });
  };

  const userNameChangeHandler = (event) => {
    setUserName(event.target.value);
  }
  return (
    <div className="container-fluid">
      <h1>Users List</h1>
      <input type="text" name="username" id="username" onChange={userNameChangeHandler}/>
      <button onClick={addUserHandler}>Add</button>
      userList.map((item)=>{item})
      {/* <UsersList userList={userList} /> */}
    </div>
  );
}

export default App;
