import "./App.css";
import React, { useState } from "react";
import UsersList from "./UsersList";

function App() {
  const [userList, setUserList] = useState([
    {
      id: Math.random().toString(),
      name: {
        salutation: "Mr",
        first: "Jatin",
        last: "Patel",
      },
    },
    {
      id: Math.random().toString(),
      name: {
        salutation: "Mrs",
        first: "Anjali",
        last: "Shah",
      },
    },

    {
      id: Math.random().toString(),
      name: {
        salutation: "Mr",
        first: "Darsh",
        last: "Gajjar",
      },
    },

    {
      id: Math.random().toString(),
      name: {
        salutation: "Mrs",
        first: "Ishita",
        last: "Verma",
      },
    },

    {
      id: Math.random().toString(),
      name: {
        salutation: "Mr",
        first: "Jatin",
        last: "Patel",
      },
    },
  ]);

  const [outputUserList, setOutputUserList] = useState(userList);

  const onInputChangeHandler = (event) => {
    const searchValue = event.target.value;
    setOutputUserList(userList.filter((item)=>item.name.salutation.includes(searchValue)||item.name.first.includes))
  };

  return (
    <div className="container-fluid">
      <input type="text" placeholder="Search" onChange={onInputChangeHandler} />
      <UsersList outputUsersList={outputUserList} />
    </div>
  );
}

export default App;
